export default function Feedback() {

    const [formData,SubmitFormData] = useState({
        name:"",
        roll:"",
        feedback:""
    })
    const [table,submitTable] = useState([])
    function HandleChange(e){
        const [name,value]=e.target;
        SubmitFormData({...formData,[name]:value});
    }
    function handleSubmit(){
        submitTable([...table,form])
    }
  return (
    <div>
        Enter Name :<input type="text" name="name" value={formData.name} onChange={HandleChange}/>
        <br/>
        Enter Roll Number : <input type='number' name="roll" value={formData.roll} onChange={HandleChange}/>
        <br/>
        <input type='text' name="fedback" placeholder="your feedback" value={formData.feedback} onChange={HandleChange}/>
        <br/>
        <button onClick={handleSubmit}>Submit</button>

        <table border={2}>
            <thead>
                <tr>
                    <th>Name</th>
                    <th>Roll No</th>
                    <th>Feedback</th>
                </tr>
                <tbody>
                   <td></td>
                   <td></td>
                   <td></td>
                </tbody>
            </thead>
        </table>
    </div>
  )
}
