import React, { useState } from 'react'
import MyTable from './MyTable';
export default function Feedback() {

    const [formData,SubmitFormData] = useState({
        name:"",
        roll:"",
        feedback:""
    })
    const [table,submitTable] = useState([])
    function HandleChange(e){
        const {name,value}=e.target;
        SubmitFormData({...formData,[name]:value});
    }
    function handleSubmit(){
        submitTable([...table,formData])
        SubmitFormData({
            name:"",
            roll:"",
            feedback:""
        })
    }
  return (
    <div>
        <input type="text" name="name" value={formData.name} onChange={HandleChange}/>
        <br/>
        <input type="number" name="roll" value={formData.roll} onChange={HandleChange}/>
        <br/>
        <input type="text"  placeholder="your feedback" name="feedback" value={formData.feedback} onChange={HandleChange}/>
        <br/>
        <button onClick={handleSubmit}>Submit</button>
        <table border={2}>
            <thead>
                <tr>
                    <th>Name</th>
                    <th>Roll No</th>
                    <th>Feedback</th>
                </tr>
            </thead>
                <tbody>

                    {table.map((key,index)=>(
                        <tr>
                        <td>{key.name}</td>
                        <td>{key.roll}</td>
                        <td>{key.feedback}</td>
                        </tr>
                    ))}

                   </tbody>


        </table>


    </div>
  )
}
