import React, { useState } from 'react'
import '../index.css'
import { Mycontext } from './Mycontext';
import { useContext } from 'react';
import { FormContext } from './Register';

export default function Login() {
  const [localDB,setDB] = useContext(FormContext);
  const [login,setLogin] = useState("");
  const [form,setForm] = useState({
    Username:"",
    Password:""

  })
  function HandleChange(e){
    const {name,value} = e.target;
    setForm({...form,[name]:value})
  }
  function HasLogin(){
    const user = localDB.find(user =>
    user.Username == form.username && user.Password ==form.Password);
    if(user){
      setLogin('Login Successful');
    }else{
      setLogin('Login Failed');
    }
  }


  return (
    <div>
    <h1 className="text-xl text-center font-bold font-mono">Login</h1>
    <form className="max-w-md mx-auto bg-white shadow-md rounded px-8 pt-6 pb-8 mb-4">
        <div className="mb-4">
        <label className="block text-gray-700 text-sm font-bold
        mb-2" htmlFor="username">
        Username
        </label>
        <input
                className="shadow appearance-none border rounded w-
                full py-2 px-3 text-gray-700 leading-tight focus:outline-none
                focus:border-blue-500"
                id="username"
                type="text"
                placeholder="Enter your username"
                onChange={HandleChange}
        />
        </div>
        <div className="mb-6">
          <label className="block text-gray-700 text-sm font-bold
              mb-2" htmlFor="password">
              Password
        </label>
        <input
                className="shadow appearance-none border rounded w-
                full py-2 px-3 text-gray-700 leading-tight focus:outline-none
                focus:border-blue-500"
                id="password"
                type="password"
                placeholder="Enter your password"
                onChange={HandleChange}
        />


        </div>
        <div className="flex items-center justify-between">
        <button
              className="bg-blue-500 hover:bg-blue-600 text-white
              font-bold py-2 px-4 rounded focus:outline-none focus:shadow-
              outline"
              type="button"
              onClick={HasLogin}>
              Sign In
        </button>
</div>
</form>
</div>


